import Task from '../Task/Task';
//import './Lane.css';
import styled from 'styled-components';
const LaneWrapper = styled.div`
 text-align: left;
 padding: 0;
 background: lightGray;
 border-radius: 20px;
 min-height: 50vh;
 width: 20vw;
 @media (max-width: 768px) {
 margin-bottom: 5%;
 }
 `;
 const Title = styled.h2`
 width: 100%;
padding-bottom: 10px;
 text-align: center;
 border-bottom: 1px solid darkGray;
 `;
 function Lane({ laneId, title, loading, error, tasks, onDragStart,onDrop,onDragOver, }) {
return (

<LaneWrapper onDragOver={onDragOver} onDrop={(e) => onDrop(e, laneId)}
>
<Title>{title}</Title>
 {loading || error ? (
 <span>{error || 'Loading...'}</span>
 ) : (
 tasks.map((task) => (
    
<Task
 key={task.id}
 id={task.id}
 title={task.title}
 body={task.body}
 onDragStart={onDragStart}
 onDrop={(e) => onDrop(e, laneId)}
 />
 ))
 )}
</LaneWrapper>
);
}
export default Lane;
